import React, { useState, useEffect } from 'react';
import './Typewriter.css'; // Make sure to import the CSS

const Typewriter = ({ text, speed = 150, onComplete }) => {
  const [index, setIndex] = useState(0);
  const [animation, setAnimation] = useState('');

  useEffect(() => {
    if (index < text.length) {
      setTimeout(() => {
        setIndex((prevIndex) => prevIndex + 1);
      }, speed);
    } else if (index === text.length) {
      // Trigger the slide and fade animation after a short delay
      setTimeout(() => setAnimation('slide-up-fade'), 500); // 500 ms delay
    }
  }, [index, text.length, speed]);

  return (
    <h1 className={animation}>
      {text.substring(0, index)}
      <span className="cursor">|</span>
    </h1>
  );
};

export default Typewriter;
